namespace MU\YourCityModule\Event\Base;

use Symfony\Component\EventDispatcher\Event;
use MU\YourCityModule\Entity\PartEntity;

/**
 * Event base class for filtering part processing.
 */
class AbstractFilterPartEvent extends Event
{
    /**
     * @var PartEntity Reference to treated entity instance.
     */
    protected $part;

    /**
     * @var array Entity change set for preUpdate events.
     */
    protected $entityChangeSet = [];

    /**
     * FilterPartEvent constructor.
     *
     * @param PartEntity $part Processed entity
     * @param array $entityChangeSet Change set for preUpdate events
     */
    public function __construct(PartEntity $part, $entityChangeSet = [])
    {
        $this->part = $part;
        $this->entityChangeSet = $entityChangeSet;
    }

    /**
     * Returns the entity.
     *
     * @return PartEntity
     */
    public function getPart()
    {
        return $this->part;
    }

    /**
     * Returns the change set.
     *
     * @return array
     */
    public function getEntityChangeSet()
    {
        return $this->entityChangeSet;
    }
}
