namespace MU\YourCityModule\Traits;

use Doctrine\ORM\Mapping as ORM;
use Symfony\Component\Validator\Constraints as Assert;

/**
 * Geographical trait implementation class.
 */
trait GeographicalTrait
{
    /**
     * The coordinate's latitude part.
     *
     * @ORM\Column(type="decimal", precision=12, scale=7)
     * @var float $latitude
     */
    protected $latitude = 0.00;
    
    /**
     * The coordinate's longitude part.
     *
     * @ORM\Column(type="decimal", precision=12, scale=7)
     * @var float $longitude
     */
    protected $longitude = 0.00;
    
    /**
     * Returns the latitude.
     *
     * @return float
     */
    public function getLatitude()
    {
        return $this->latitude;
    }
    
    /**
     * Sets the latitude.
     *
     * @param float $latitude
     *
     * @return void
     */
    public function setLatitude($latitude)
    {
        if (floatval($this->latitude) !== floatval($latitude)) {
            $this->latitude = floatval($latitude);
        }
    }
    
    /**
     * Returns the longitude.
     *
     * @return float
     */
    public function getLongitude()
    {
        return $this->longitude;
    }
    
    /**
     * Sets the longitude.
     *
     * @param float $longitude
     *
     * @return void
     */
    public function setLongitude($longitude)
    {
        if (floatval($this->longitude) !== floatval($longitude)) {
            $this->longitude = floatval($longitude);
        }
    }
    
}
